import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './NewsFeed.css'

const Favorites = () => {
    const [favorites, setFavorites] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchFavorites = async () => {
            try {
                const response = await axios.get('/api/favorites');
                setFavorites(response.data);
                setIsLoading(false);
            } catch (err) {
                setError(err);
                setIsLoading(false);
            }
        };

        fetchFavorites();
    }, []);

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error loading favorites.</div>;

    return (
        <div className="news-feed">
            <h1>Your Favorites</h1>
            <div className="articles-list">
                {favorites.map(fav => (
                    <div className="article" key={fav._id}>
                        <h2 className="article-title">{fav.title}</h2>
                        <p className="article-description">{fav.description}</p>
                        {fav.imageUrl && <img src={fav.imageUrl} alt={fav.title} className="article-image" />}
                        <a href={fav.url} target="_blank" rel="noopener noreferrer" className="article-link">Read full article</a>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Favorites;
