// NewsFeed.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const NewsFeed = () => {
    const [articles, setArticles] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchArticles = async () => {
            try {
                const response = await axios.get('http://localhost:9000/api/news/latest');
                setArticles(response.data.results); // Adjust according to the API response
                setIsLoading(false);
            } catch (err) {
                setError(err);
                setIsLoading(false);
            }
        };

        fetchArticles();
    }, []);

    if (isLoading) return <div>Loading...</div>;
    if (error) return <div>Error loading articles.</div>;

    return (
        <div>
            <h1>News Feed</h1>
            <ul>
                {articles.map(article => (
                    <li key={article.id}>{article.title}</li> // Adjust according to the data structure
                ))}
            </ul>
        </div>
    );
};

export default NewsFeed;
